INSERT INTO `app_travelinfo` VALUES ('1031', '珠海凤凰山', '未评价', '10', '37', '珠海', '49', '珠海市香洲区城区北面旅游路', '被原始森林覆盖的大山', 'https://piao.qunar.com/ticket/detail_4098063170.html', '5', '188', '62', '珠海凤凰山地处北回归线以南，位于珠海市城区北面，属沿海丘陵地区，山地是典型的南亚热带季风气候，气温高，热量丰富，雨量充沛。凤凰山区森林植被覆盖率达90％，植被类型为南亚热带常绿阔叶林群落。凤凰山树木种类繁多，天然乔木树种主要有阴香、山乌桕、鸭脚木、小叶榕、高山榕、孔雀豆、山龙眼、猴耳环等，人工栽培的乔木树种主要有马尾松、湿地松、台湾相思、大叶相思、按树类等。凤凰山一带地形地貌丰富多样，山岳峦峰较多，海拔超过150米的山峰有凤凰山、大南山、小南山、大径顶、枇杷地、白鹤顶、望天狮、尖山、真子排顶、红花山、南锅神、周坑山、径东山、白沙岭、鸭贵门等。凤凰山水源充沛，区内溪流众多，现有珠海水库、大镜山水库、梅溪水库、正坑水库、青年水库。', '[\"https://imgs.qunarzz.com/sight/p8/201211/00/6671c02073f13b2f93835fbb.jpg_710x360_f24ba16c.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0102r1200098wpquv016A.jpg_710x360_51784889.jpg\", \"https://imgs.qunarzz.com/sight/p0/1412/65/0094d4be61f8757ba61c621c766bfd53.water.jpg_710x360_1b410bc7.jpg\", \"https://imgs.qunarzz.com/sight/p0/1412/ac/17617d08819bfb012b7ec55e37b57bfd.water.jpg_710x360_5e3033f3.jpg\", \"https://imgs.qunarzz.com/sight/p0/1412/cb/c7931a8e8908177c8c6e3ebfa48ce713.water.jpg_710x360_6b9e6a03.jpg\", \"https://imgs.qunarzz.com/sight/p0/1412/6e/f1aa2faf81368eea8555b1e83c43316e.water.jpg_710x360_8b9b3715.jpg\"]', '[{\"author\": \"1*7\", \"content\": \"\\u73af\\u5883\\u975e\\u5e38\\u4e0d\\u9519\\uff0c\\u5f88\\u51fa\\u7247\\uff0c\\u63a8\\u8350\\u60f3\\u62cd\\u7167\\u7684\\u59d0\\u59b9\\u6765\\uff01\\u653e\\u5fc3\\u6765\\u5c31\\u5bf9\\u4e86\\uff0c\\u4f53\\u9a8c\\u611f\\u4e00\\u7ea7\\u68d2\\uff0c\\u603b\\u4e4b\\u975e\\u5e38\\u63a8\\u8350\\u5404\\u4f4d\\u6765\\u6253\\u5361\", \"date\": \"2022-07-11\", \"score\": 5}]', 'https://imgs.qunarzz.com/sight/p8/201211/00/6671c02073f13b2f93835fbb.jpg_280x200_f980cbf4.jpg', '2023-08-03');
INSERT INTO `app_travelinfo` VALUES ('1032', '东澳岛', '4A景区', '10', '418', '珠海', '48', '珠海市香洲区', '享热带风情访历史遗迹', 'https://piao.qunar.com/ticket/detail_250211386.html', '1', '88', '1245', '东澳岛位于万山群岛中部，面积4.62平方公里，东澳岛四周海域水质清澈，毫无污染。冲浪，潜水，风帆等是这里的主要旅游项目。岛上有住宿服务。岛的东部有建于清乾隆年间的古堡铳城，密林中隐著“万海平波”等石刻，中部有汉白玉送子观音像及送子仙泉；南部有水清沙幼的南沙滩；沿通天古道直上斧担山顶，可观日月海，相映生辉。在万山群岛众多岛屿中。东澳岛林木葱郁，有的地方古树参天。踏上东澳，蔚蓝色的海水，绿色的山野，袅袅的渔村炊烟，岸边停泊的艘艘的渔船、舢板，使你顿有走进世外桃源的感觉，抛却久居都市的喧嚣与烦扰。', '[\"https://imgs.qunarzz.com/sight/p0/201305/29/8998a28b80a1c5f2c8d65eac.jpg_710x360_39c511e6.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0103k120008ov3j70E18C.jpg_710x360_84055629.jpg\", \"https://imgs.qunarzz.com/sight/p0/1411/1e/a54eae512b7c52fe56af9c04a4f13c8d.water.jpg_710x360_533ad85d.jpg\", \"https://imgs.qunarzz.com/sight/p0/201301/17/aa507ef7c2c22a1b93835fbb.jpg_710x360_a184078d.jpg\", \"https://imgs.qunarzz.com/sight/p0/201305/29/d3bc74a2cfc12416c8d65eac.jpg_710x360_4233a83d.jpg\", \"https://imgs.qunarzz.com/sight/p0/201305/29/191adf91a98e386fc8d65eac.jpg_710x360_b0884ecd.jpg\"]', '[{\"author\": \"\\u5361*\\u8138\", \"content\": \"\\u9152\\u5e97\\u670d\\u52a1\\u7279\\u522b\\u7684\\u597d\\uff0c\\u7279\\u522b\\u662f\\u7ba1\\u5bb6Kevin\\u548c\\u7ba1\\u5bb6ann\\uff0c\\u4e0b\\u6b21\\u9996\\u9009\\uff0c\", \"date\": \"2023-07-04\", \"score\": 5}, {\"author\": \"\\u53bb*\\u6237\", \"content\": \"\\u5dee\\u52b2\\u7684\\u53bb\\u54ea\\u513f\\u5ba2\\u670d\\uff0c\\u5dee\\u52b2\\u4e1c\\u6fb3\\u5c9b\\u5546\\u5bb6\\u3002\\u540c\\u65f6\\u5b9a\\u7684\\u5f80\\u8fd4\\u7968\\uff0c\\u7ed3\\u679c\\u53bb\\u7a0b\\u7968\\u6263\\u94b1\\u4e86\\uff0c\\u4e00\\u76f4\\u672a\\u663e\\u793a\\u9884\\u8ba2\\u6210\\u529f\\uff0c\\u56de\\u7a0b\\u53cd\\u800c\\u5feb\\u5feb\\u663e\\u793a\\u9884\\u8ba2\\u6210\\u529f\\uff0c\\u4e24\\u4e2a\\u5c0f\\u65f6\\u4ee5\\u540e\\u53d1\\u73b0\\u8fd8\\u672a\\u6210\\u529f\\uff0c\\u4fbf\\u6253\\u4eba\\u5de5\\u5ba2\\u670d\\uff0c\\u624d\\u544a\\u77e5\\u6ca1\\u6709\\u7968\\u4e86\\uff0c\\u7ed9\\u6211\\u9000\\u94b1\\