INSERT INTO `app_travelinfo` VALUES ('1155', '大龙湖', '4A景区', '5', '65', '南宁', '48', '南宁市上林县西燕镇', '湖被群山环抱，湖岸有山洞', 'https://piao.qunar.com/ticket/detail_3199182978.html', '4.8', '9', '142', '位于广西南宁市上林县西燕镇，南宁至梧州二级公路（324国道），从上林至马山二级公路到西燕，再沿西燕至大龙湖旅游公路可达，距首府南宁市160千米。大龙湖是人工拦蓄地下水而成的人造高山平湖，属喀斯特溶岩地貌高山平湖型自然风景旅游区。景区由大龙湖和大龙洞两部分组成，总库容为1.51亿立方米180多米，湖中有14个天然小岛，湖面碧水连天，四周群山环抱，石峰依肩并立，青山倒影浑然一体。依傍湖岸的大龙洞有12个游览厅，每个大厅各有风光，钟乳石千姿百态，形状怪异。洞顶有3个天然的天窗，洞中有水，水中有洞，洞中见天，深邃曲折，奇景荟萃，洞顶悬石参差，景物盘...', '[\"https://imgs.qunarzz.com/sight/p43/201211/02/8b7209e61e2bb71993835fbb.jpg_710x360_cf52f4b3.jpg\", \"https://imgs.qunarzz.com/tuan/team2/1507/de/1065c0bac6ab49.jpg_710x360_4514cd5a.jpg\", \"https://imgs.qunarzz.com/tuan/team2/1507/21/b28c97d8826dc3.jpg_710x360_f66acc0b.jpg\", \"https://imgs.qunarzz.com/tuan/team2/1507/b9/4376d0aaed4706.jpg_710x360_7f389d12.jpg\", \"https://imgs.qunarzz.com/tuan/team2/1507/4d/f62aaa90f786f3.jpg_710x360_cf7728e7.jpg\", \"https://imgs.qunarzz.com/tuan/team2/1507/2a/dbc4a5366536c7.jpg_710x360_a71545a5.jpg\"]', '[]', 'https://imgs.qunarzz.com/sight/p43/201211/02/8b7209e61e2bb71993835fbb.jpg_280x200_c6cf963e.jpg', '2023-08-03');
INSERT INTO `app_travelinfo` VALUES ('1156', '南宁人民公园', '4A景区', '5', '4', '南宁', '47', '南宁市兴宁区新民路44号', '登望仙坡俯瞰城区风光', 'https://piao.qunar.com/ticket/detail_97000608.html', '1', '9', '91', '人民公园又称白龙公园，因园内有一约七万平方米的白龙湖而得名。据宋史《舆地纪胜》记载，宋代名将狄青将军曾率军队在此驻扎，某日看见湖边有一群白羊，好像一条游动的白龙，所以就给此湖起名为白龙湖，可见白龙湖的得名已有上千年的历史了。', '[\"https://imgs.qunarzz.com/sight/p23/201211/00/d56e87974402bc3f93835fbb.jpg_710x360_5ff4614e.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0106w120005wn20mrE919.jpg_710x360_00a7ff17.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/100t1f000001gqblj8596.jpg_710x360_f76037bb.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/f51be63f84ff87.jpg_710x360_1554e09b.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/3561c938a00dfb37.jpg_710x360_14cadd9a.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0a1d31e63a658c.jpg_710x360_28ded12b.jpg\"]', '[{\"author\": \"t*G\", \"content\": \"\\u6de6\\uff0c\\u4eba\\u6c11\\u516c\\u56ed\\u514d\\u8d39\\uff0c\\u7136\\u540e\\u8fd9\\u91cc\\u663e\\u793a\\u7684\\u662f\\u6d77\\u5e95\\u4e16\\u754c\\u95e8\\u7968\\uff0c\\u70b9\\u8fdb\\u53bb\\u4e70\\u4e86\\u53d1\\u73b0\\u662f\\u4ec0\\u4e48\\u5582\\u9c7c\\u7684\\u7968\\uff0c\\u80fd\\u4e0d\\u80fd\\u505a\\u70b9\\u4eba\\u5e72\\u7684\\u4e8b\\uff1f\\uff1f\\uff1f\", \"date\": \"2023-01-07\", \"score\": 1}, {\"author\": \"\\u96ea*2\", \"content\": \"\\u4eba\\u6c11\\u516c\\u56ed\\u662f\\u514d\\u8d39\\u7684\\uff0c\\u5c31\\u5728\\u5b98\\u5858\\u5e02\\u573a\\u9644\\u8fd1\", \"date\": \"2022-02-14\", \"score\": 1}]', 'https://imgs.qunarzz.com/sight/p23/201211/00/d56e87974402bc3f93835fbb.jpg_280x200_d8a3f38f.jpg', '2023-08-03');
INSERT INTO `app_travelinfo` VALUES ('1157', '龙门水都', '4A景区', '5.9', '44', '南宁', '49', '南宁市西乡塘区振兴路那秧坡附近', '四季常青的生态氧吧', 'https://piao.qunar.com/ticket/detail_1615698442.html', '4.9', '29.9', '4311', '龙门水都生态旅游景区，将观景与游乐、消夏与养生、休闲与文化有机契合，打造出传统与现代“无缝对接”的时尚水乡。', '[\"https://imgs.qunarzz.com/sight/p0/1807/3d/3d5f30dcfee51505a3.img.jpg_710x360_9a973825.jpg\", \"https://imgs.qunarzz.com/sight/p0/1807/af/af7de58ce158aea1a3.img.jpg_710x360_2de7f5fd.jpg\", \"https://imgs.qunarzz.com/sight/p0/1807/ef/ef6a03e81df72677a3.img.jpg_710x360_88fb8913.jpg\", \"https://imgs.qunarzz.com/sight/p0/1807/f9/f92ecf84e1ade5fca3.img.jpg_710x360_01ad09c5.jpg\", \"https://imgs.qunarzz.com/sight/p0/1807/18/18dcc8106cd5209ca3.img.jpg_710x360_63287ce2.jpg\", \"https://imgs.qunarzz.com/sight/p0/1807/7e/7ee916be753eb86ea3.img.jpg_710x360_51047950.jpg\"]', '[]', 'https://imgs.qunarzz.com/sight/p0/1807/3d/3d5f30dcfee51505a3.img.jpg_280x200_b7bf4709.jpg', '2023-08-03');
INSERT INTO `app_travelinfo` VALUES ('1158', '广西高峰森林公园', '4A景区', '8.3', '14', '南宁', '49', '南宁市兴宁区X03745(邕武路)', '赏森林湖泊，访精灵王国', 'https://piao.qunar.com/ticket/detail_1012813834.html', '4.9', '30', '352', '广西高峰森林公园地处素有“绿城”之誉的广西壮族自治区首府南宁北郊，距南宁市区中心11公里（邕武路城市主干道路），公园主入口距离南宁外环高速公路高峰收费站仅4公里，交通十分便利。项目总占地18000亩，其中核心区约8000余亩，旨在打造成为环城森林休闲旅游示范圈中的动感休闲旅游名片。', '[\"https://imgs.qunarzz.com/sight/p0/1904/ab/abd559320f4e7572a3.img.jpg_710x360_83634002.jpg\", \"https://imgs.qunarzz.com/sight/p0/1904/36/360160096271fabca3.img.jpg_710x360_5e50043f.jpg\", \"https://imgs.qunarzz.com/sight/p0/1904/f3/f31eca475158ab59a3.img.jpg_710x360_4575dffb.jpg\", \"https://imgs.qunarzz.com/sight/p0/1904/d8/d848a16453472014a3.img.jpg_710x360_402db802.jpg\", \"https://imgs.qunarzz.com/sight/p0/1904/55/55a872b631243193a3.img.jpg_710x360_43a7cfd4.jpg\", \"https://imgs.qunarzz.com/sight/p0/1904/45/45b8b47fe119524a3.img.jpg_710x360_cf32cbe1.jpg\"]', '[]', 'https://imgs.qunarzz.com/sight/p0/1904/ab/abd559320f4e7572a3.img.jpg_280x200_1d3af2bf.jpg', '2023-08-03');
INSERT INTO `app_travelinfo` VALUES ('1159', '南宁极地海洋世界', '4A景区', '10', '374', '南宁', '50', '南宁市江南区沙井大道与亭洪路交叉口东200米路北', '感受海洋，科技带来的乐趣', 'https://piao.qunar.com/ticket/detail_2543759806.html', '5.0', '129', '2711', '南宁融晟极地海洋世界成立于2018年，是融晟集团携手海昌海洋公园，共同打造广西首家大型极地海洋世界。', '[\"https://imgs.qunarzz.com/sight/p0/1808/5d/5d39969e38b89767a3.img.jpg_710x360_d7ff1eb9.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0105g120008hr8xbr4E86.jpg_710x360_83526039.jpg\", \"https://imgs.qunarzz.com/sight/p0/1808/7a/7a6fd16773a385aca3.img.jpg_710x360_4f44f6dc.jpg\", \"https://imgs.qunarzz.com/sight/p0/1808/39/39df6f29fb2a3f4fa3.img.jpg_710x360_3ff65271.jpg\", \"https://imgs.qunarzz.com/sight/p0/1808/99/99b48276c48287a3.img.jpg_710x360_f63be629.jpg\", \"https://imgs.qunarzz.com/sight/p0/1808/1f/1fe94a87061de136a3.img.jpg_710x360_4ef5833e.jpg\"]', '[]', 'https://imgs.qunarzz.com/sight/p0/1808/5d/5d39969e38b89767a3.img.jpg_280x200_e18b6aa6.jpg', '2023-08-03');
INSERT INTO `app_travelinfo` VALUES ('1160', '南宁花花大世界', '4A景区', '10', '12', '南宁', '49', '南宁市武鸣县双桥镇伊岭工业区C—3', '赏四季花卉并亲近动物', 'https://piao.qunar.com/ticket/detail_1748306452.html', '2', '40', '1319', '南宁花花大世界园林产业示范园位于南宁市武鸣县双桥镇，由南宁花花大世界园林有限公司管理，是南宁市“136”重点工程建设项目之一，距南宁市仅16公里，都南高速公路和南武二级公路擦肩而过，交通极为便利。园区始建于2003年7月28日，于2005年10月1日正式对外开放。', '[\"https://imgs.qunarzz.com/sight/p0/1507/93/57eaeca1cec24d02beeb5c31f3814562.water.jpg_710x360_08e8506c.jpg\", \"https://imgs.qunarzz.com/sight/p0/201301/10/ae197788ca78ae2e93835fbb.jpg_710x360_5ebf9ed3.jpg\", \"https://imgs.qunarzz.com/sight/p0/201301/10/d75750bf6330cee793835fbb.jpg_710x360_599fcbb1.jpg\", \"https://imgs.qunarzz.com/sight/p0/201301/10/d1dafe43d6b9f8a893835fbb.jpg_710x360_8489eb46.jpg\", \"https://imgs.qunarzz.com/sight/p0/201301/10/d7d1c7e2645cedd593835fbb.jpg_710x360_c2a31dc0.jpg\", \"https://imgs.qunarzz.com/sight/p0/201301/10/a71c52d7aae5e9e193835fbb.jpg_710x360_84fcd646.jpg\"]', '[{\"author\": \"\\u53bb*\\u6237\", \"content\": \"\\u5468\\u672b\\u4e00\\u5bb6\\u5b50\\u4e94\\u4e2a\\u4eba\\u53bb\\u73a9\\uff0c\\u5176\\u4e2d\\u4e24\\u4e2a\\u662f\\u5c0f\\u5b69\\uff0c\\u6700\\u5c0f\\u7684\\u662f\\u5a74\\u513f\\uff0c\\u63d0\\u524d\\u4e70\\u4e86\\u95e8\\u7968\\uff0c\\u5230\\u95e8\\u53e3\\u9a8c\\u7968\\u88ab\\u544a\\u77e5\\u7f51\\u4e0a\\u4e70\\u7968\\u9700\\u8981\\u7b49\\u4e00\\u4e2a\\u5c0f\\u65f6\\u624d\\u80fd\\u8fdb\\u53bb\\uff0c\\u8ddf\\u5de5\\u4f5c\\u4eba\\u5458\\u6c9f\\u901a\\uff0c\\u56e0\\u4e3a\\u662f\\u7b2c\\u4e00\\u6b21\\u53bb\\uff0c\\u4e24\\u4e2a\\u5c0f\\u5b69\\u53c8\\u5c0f\\uff0c\\u666f\\u533a\\u6e38\\u73a9\\u7684\\u4eba\\u53c8\\u5f88\\u5c11\\uff0c\\u80fd\\u4e0d\\u80fd\\u901a\\u878d\\u4e00\\u4e0b\\uff0c\\u5de5\\u4f5c\\u4eba\\u5458\\u6001\\u5ea6\\u5f88\\u51b7\\u6f20\\uff0c\\u677f\\u7740\\u4e2a\\u8138\\u4e0d\\u7406\\u4e0d\\u776c\\uff0c\\u5f53\\u65f6\\u771f\\u7684\\u5f88\\u751f\\u6c14\\uff0c\\u4ece\\u5e02\\u91cc\\u5f00\\u8f66\\u90a3\\u4e48\\u4e45\\u7279\\u610f\\u53bb\\u73a9\\uff0c\\u4e5f\\u602a\\u6211\\u4eec\\u6ca1\\u6