INSERT INTO `app_travelinfo` VALUES ('1164', '水锦顺庄旅游景区', '4A景区', '10', '84', '南宁', '50', '南宁市马山县古零镇乐平村水锦屯', '天然清凉大氧吧', 'https://piao.qunar.com/ticket/detail_4290686166.html', '5.0', '54', '790', '水锦·顺庄旅游景区四周环大明山，背面水锦溪河贯穿而过，三面环水，与约10公里长的天然峡谷以及瀑布、清泉池、溪流交相辉映，枕高山，面流水，构成一幅令人惊叹的山水画卷。景区规划主要分为峡谷旅游区、生态农业展示区和主题度假酒店三大区。其中，主题度假酒店是一个集旅游度假、休闲娱乐、养生、观光农业等多种配套服务*能为一体的综合性休闲旅游度假山庄。建筑风格简洁、大气，在规划布局上依据现状地形，顺应山体走势散状分布，让游客枕高山、面流水，人、山、水、庄融为一体，达到“天人合一”的至善境界。目前九叠泉、观光步行栈道建、度假酒店一期、农家乐、水上娱乐体验区、农家旅馆等景点已建成对外开放。', '[\"https://imgs.qunarzz.com/sight/p0/1807/5e/5e7d8138af45b6a7a3.img.jpg_710x360_4a61ea13.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0106u120008ikbpjj7B04.jpg_710x360_1ad60562.jpg\", \"https://imgs.qunarzz.com/sight/p0/1807/f6/f64a09e0ef1b1b66a3.img.jpg_710x360_f1460878.jpg\", \"https://imgs.qunarzz.com/sight/p0/1807/60/60b673758c198614a3.img.jpg_710x360_ca611339.jpg\", \"https://imgs.qunarzz.com/sight/p0/1807/7c/7c504ef8030fe74aa3.img.jpg_710x360_f234d87a.jpg\", \"https://imgs.qunarzz.com/sight/p0/1807/f5/f5f2c2ddac10e04aa3.img.jpg_710x360_56d7b463.jpg\"]', '[]', 'https://imgs.qunarzz.com/sight/p0/1807/5e/5e7d8138af45b6a7a3.img.jpg_280x200_b6a80bb1.jpg', '2023-08-03');
INSERT INTO `app_travelinfo` VALUES ('1165', '九曲湾温泉度假村', '4A景区', '9.9', '64', '南宁', '49', '南宁市兴宁区三塘镇温泉路9号', '融合壮乡文化与秀丽风光', 'https://piao.qunar.com/ticket/detail_2661094804.html', '5', '158.8', '1746', '九曲湾温泉度假村位于南宁市三塘镇，坐落在风光秀丽的九曲湾农场，是一个以温泉生态旅游为主轴，融游览、观光度假、休闲娱乐、康体疗养、酒店、会议、文化、地产开发等经营项目于一体，具有浓郁民族风情和地方特色的温泉旅游度假胜地。', '[\"https://imgs.qunarzz.com/sight/p0/2209/3f/3f7d096fc6ba2a8a3.water.jpg_710x360_943b295d.jpg\", \"https://imgs.qunarzz.com/sight/p0/2209/b9/b9bf42a41880665aa3.water.jpg_710x360_378d1a71.jpg\", \"https://imgs.qunarzz.com/sight/p0/2209/2b/2baffdbbc1c3f24ea3.water.jpg_710x360_2d669f6d.jpg\", \"https://imgs.qunarzz.com/sight/p0/2209/88/880059e78980f24aa3.water.jpg_710x360_3d5c627d.jpg\", \"https://imgs.qunarzz.com/sight/p0/2209/3a/3a4827d376b4ac72a3.water.jpg_710x360_adecd3cc.jpg\", \"https://imgs.qunarzz.com/sight/p0/2209/26/26eee6f881a56b6ba3.water.jpg_710x360_d22d659c.jpg\"]', '[{\"author\": \"n*6\", \"content\": \"\\u7b2c\\u4e00\\u6b21\\u4e3b\\u52a8\\u8bc4\\u4ef7\\uff0c\\u53bb\\u8fc7\\u5f88\\u591a\\u6b21\\u5357\\u5b81\\u8fd8\\u4e0d\\u77e5\\u9053\\u79bb\\u5e02\\u533a\\u5f88\\u8fd1\\u7684\\u5730\\u65b9\\u6709\\u4e00\\u4e2a\\u8fd9\\u4e48\\u597d\\u8fd9\\u4e48\\u4fbf\\u5b9c\\u7684\\u6237\\u5916\\u6e29\\u6cc9\\uff0c\\u6e29\\u6cc9\\u9152\\u5e97\\u9644\\u8fd1\\u5c31\\u662f\\u5965\\u7279\\u83b1\\u65af\\uff0c\\u521a\\u521a\\u529e\\u7406\\u95e8\\u7968\\uff0c\\u5e97\\u5458\\u5c31\\u8ba9\\u6211\\u4eec\\u62bd\\u4e86\\u7ea2\\u5305\\uff0c\\ud83d\\ude04\\u95e8\\u7968\\u5305\\u542b\\u7684\\u4e0b\\u5348\\u8336\\u4e5f\\u5f88\\u68d2\\uff0c\\u975e\\u9e21\\u808b\\uff0c\\u4e0d\\u9700\\u8981\\u51c6\\u5907\\u96f6\\u98df\\uff0c\\u8fd9\\u91cc\\u7684\\u9910\\u5385\\u53e3\\u5473\\u5f88\\u597d\\u91cf\\u5927\\u4ef7\\u683c\\u4e5f\\u5f88\\u4f4e\\uff0c\\uff08\\u8001\\u53cb\\u7c89\\u6bd4\\u5916\\u9762\\u7684\\u597d\\u5403\\ud83d\\ude0b\\uff09\\u5403\\u5b8c\\u996d\\u8fd8\\u9001\\u4e86\\u4e24\\u5f20\\u52b5\\u5151\\u6362\\u793c\\u54c1\\uff0c\\u51fa\\u95e8\\u9001\\u4e86\\u4e24\\u74f6\\u6b63\\u88