INSERT INTO `app_travelinfo` VALUES ('1183', '大桂山国家森林公园', '4A景区', '10', '4', '贺州', '48', '广西壮族自治区贺州市八步区步头镇', '总面积40平方公里', 'https://piao.qunar.com/ticket/detail_1526280686.html', '4.8', '65', '110', '大桂山国家森林公园始建于1993年。位于湘、桂、粤三省交界处，距贺州市区约30公里，是广州至桂林旅游黄金线（207国道）。景区面积约5万公顷、森林覆盖率达93%，负氧离子含量为69120/cm3。奇藤异树品类繁多，峡谷、清溪、飞瀑随处可见、是一个天然植物王国和天然氧吧。景区游览线路大部分由木栈道桥贯穿而成，景点上百个。主要景区景点有云栈湖山水实景风情演出、天槽峡谷、巨藤王国、五马归槽瀑布、留羊顶、土匪窝旧址、空中溜索等。', '[\"https://imgs.qunarzz.com/sight/p11/201211/01/62243ebbe1bd578693835fbb.jpg_710x360_784cc74f.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/010361200085m77sf726A.jpg_710x360_793edd35.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0106x120008a04i1e247E.jpg_710x360_962f219d.jpg\"]', '[]', 'https://imgs.qunarzz.com/sight/p11/201211/01/62243ebbe1bd578693835fbb.jpg_280x200_61b6a07c.jpg', '2023-08-03');
INSERT INTO `app_travelinfo` VALUES ('1184', '姑婆山', '4A景区', '9', '678', '贺州', '46', '贺州市平桂区姑婆山自然保护区', '赏茶园瀑布与秀美山水', 'https://piao.qunar.com/ticket/detail_3177334510.html', '5', '60', '7891', '姑婆山景区以秀美的山势、灵透的瀑布河流和古老茂密的森林风光为主，是一处环境优美的天然氧吧。另外，这里还是多部电视剧的取景地，《茶是故乡浓》中的方家茶园，《酒是故乡醇》中的九铺香酒厂，《春蚕织梦》中的江南水乡锦绣村在这里都依稀可见。', '[\"https://imgs.qunarzz.com/sight/p0/201405/26/1c5c414233819655305dedd2e6d44d35.jpg_710x360_adddcd77.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/01005120008fyhob16E5F.jpg_710x360_913c85e8.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0103d1200086ruea5C06C.jpg_710x360_ce6d49ee.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0100p120008alxt9e7422.jpg_710x360_796e327f.jpg\", \"https://imgs.qunarzz.com/sight/p0/1903/eb/eb94d9369ab40d1fa3.img.jpg_710x360_6be27969.jpg\", \"https://imgs.qunarzz.com/sight/p0/1903/c6/c6d5d7711a435f04a3.img.jpg_710x360_6ee23a3a.jpg\"]', '[{\"author\": \"v*7\", \"content\": \"\\u98ce\\u666f\\u597d\\uff0c\\u670d\\u52a1\\u4e5f\\u4e0d\\u9519\\uff0c\\u503c\\u5f97\\u53bb\\u770b\\u4e00\\u770b\", \"date\": \"2023-05-23\", \"score\": 5}, {\"author\": \"s*\\u8096\", \"content\": \"\\u7f51\\u7edc\\u7968\\u4e0d\\u9700\\u8981\\u53d6\\u7968\\uff0c\\u626b\\u4e8c\\u7ef4\\u7801\\u8fdb\\u53bb\\u5c31\\u53ef\\u4ee5\\u4e86\\u3002\\u5e74\\u8f7b\\u4eba\\u4e0d\\u9700\\u8981\\u4e70\\u89c2\\u5149\\u8f66\\u7968\\uff0c\\u6b65\\u884c\\u4e00\\u5c0f\\u65f6\\u5c31\\u5230\\u4ed9\\u59d1\\u7011\\u5e03\\u3002\\u4ece\\u4ed9\\u59d1\\u7011\\u5e03\\u767b\\u9876\\u7684\\u8bdd\\uff0c\\u8d70\\u4e00\\u5708\\u73af\\u5f62\\u6b65\\u9053\\u89815\\uff0d7\\u4e2a\\u5c0f\\u65f6\\u3002\", \"date\": \"2023-05-03\", \"score\": 4}, {\"author\": \"n*5\", \"content\": \"\\u98ce\\u666f\\u4f18\\u7f8e\\uff0c\\u5c31\\u662f\\u5bf9\\u5047\\u671f\\u53bb\\u7684\\u8bdd\\uff0c\\u89c2\\u5149\\u8f66\\u4e0d\\u591f\\u591a\\uff0c\\u6392\\u961f\\u592a\\u957f\\u3002\", \"date\": \"2023-05-02\", \"score\": 5}, {\"author\": \"\\u53bb*\\