INSERT INTO `app_travelinfo` VALUES ('1232', '象州温泉源头宾馆温泉', '未评价', '10', '75', '来宾', '50', '热水村源头宾馆内', '天然的温泉大浴池', 'https://piao.qunar.com/ticket/detail_653633158.html', '5.0', '17', '5', '象州温泉源头宾馆位于象州温泉源头景区周边，宾馆内有濮泉宫，天然的温泉大浴池可同时容纳上百人泡汤，源头天然烫蛋池40分钟可以烫熟鸡蛋、鹌鹑蛋，且特别细嫩鲜滑，香甜可口。象州温泉，旧名濮水名泉，当地群众传统俗称“热水”，温泉之水来自于离地面几百米深处的热岩，泉水清澈透明。', '[\"https://imgs.qunarzz.com/sight/p0/2107/1f/1f3b97d9bc5e7f98a3.img.jpg_710x360_5f831fc3.jpg\", \"https://imgs.qunarzz.com/sight/p0/2107/1e/1e3974652c976465a3.img.jpg_710x360_fed28fcf.jpg\", \"https://imgs.qunarzz.com/sight/p0/2107/75/75d3168c25503caba3.img.jpg_710x360_204a6a24.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/ca8f3513b485f2f947ee59ab7f113710.jpg_710x360_5f139ded.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/9daa97236c013537.jpg_710x360_9e1e38fb.jpg\"]', '[]', 'https://imgs.qunarzz.com/sight/p0/2107/1f/1f3b97d9bc5e7f98a3.img.jpg_280x200_36a78a40.jpg', '2023-08-03');
INSERT INTO `app_travelinfo` VALUES ('1233', '柳州克里湾水乐园', '4A景区', '10', '28', '柳州', '50', '柳州市柳东新区九子岭大道36号', '内有动感十足的冲浪池', 'https://piao.qunar.com/ticket/detail_421144992.html', '5.0', '138', '1494', '柳州克里湾水乐园是以水娱乐为核心，营造的蓝色浪漫大型主题体验型室外水乐园。位于柳州市柳东新区九子岭大道以东，毗邻柳州园博园景区，园区分为中心广场、海地之城、海地幻境、海地沙滩、海地探险等片区。每个区域都将设置不同的“故事线”，让游客身在柳州，也能感受到媲美地中海的碧海蓝天。', '[\"https://imgs.qunarzz.com/sight/p0/1907/fc/fc7bb1ac3034367a3.img.png_710x360_20b066bc.png\", \"https://imgs.qunarzz.com/sight/p0/1907/8b/8bf0d459e2698cd9a3.img.jpg_710x360_ecb18ad7.jpg\", \"https://imgs.qunarzz.com/sight/p0/1907/5a/5a43230851b63506a3.img.jpg_710x360_af4d91e7.jpg\", \"https://imgs.qunarzz.com/sight/p0/1907/de/de11f9f9c798718fa3.img.jpg_710x360_4c0a25e1.jpg\", \"https://imgs.qunarzz.com/sight/p0/1905/5d/5dcef37ddd324b07a3.img.jpg_710x360_3a73da63.jpg\", \"https://imgs.qunarzz.com/sight/p0/1905/29/2965861e73d825da3.img.jpg_710x360_edfb3a48.jpg\"]', '[]', 'https://imgs.qunarzz.com/sight/p0/1907/fc/fc7bb1ac3034367a3.img.png_280x200_3975de04.png', '2023-08-03');
INSERT INTO `app_travelinfo` VALUES ('1234', '双龙沟', '4A景区', '10', '64', '柳州', '50', '柳州市融水苗族自治县古选村', '天然大氧吧', 'https://piao.qunar.com/ticket/detail_381483193.html', '5.0', '80', '801', '双龙沟位于融水苗族自治县天山北坡沟谷带，北距沙湾县城约75公里，是一处以原始深山野林探险为特色的旅游胜地。入口处大量飞舞的蜻蜓，使这里形成了天然的迎客生物帐。', '[\"https://imgs.qunarzz.com/sight/p0/1707/81/811dafed2d5c1813a3.water.jpg_710x360_cc2086fe.jpg\", \"https://imgs.qunarzz.com/sight/p0/1804/26/2628b65a4f72c230a3.img.jpg_710x360_3ee3e4b2.jpg\", \"https://imgs.qunarzz.com/sight/p0/1804/56/565c03a15f1d5dc6a3.img.jpg_710x360_23653ea5.jpg\", \"https://imgs.qunarzz.com/sight/p0/1804/2c/2cd7ca5313f30bf3a3.img.jpg_710x360_f4b7ffd5.jpg\", \"https://imgs.qunarzz.com/sight/p0/1804/cd/cdafb79b944073e4a3.img.jpg_710x360_a5276ca1.jpg\", \"https://imgs.qunarzz.com/sight/p0/1804/8f/8ff2ed7b3900a50ca3.img.jpg_710x360_90b25feb.jpg\"]', '[]', 'https://imgs.qunarzz.com/sight/p0/1707/81/811dafed2d5c1813a3.water.jpg_280x200_c290b750.jpg', '2023-08-03');
INSERT INTO `app_travelinfo` VALUES ('1235', '三江县仙人山景区', '4A景区', '10', '40', '柳州', '50', '柳州市三江侗族自治县八江乡布央村仙人山茶场', '高山茶场云雾环绕四季如春', 'https://piao.qunar.com/ticket/detail_2865823349.html', '5', '38', '299', '三江仙人山景区位于布央仙人山位于湘黔桂三省交界地带，三江县的北部，三江县八江镇布央村。该处山峦起伏，温润多雾，绿林苍翠，溪涧纵横，茶园分布在海拔600米------1000米之处,方圆几百里之内没有任何工业污染。这里的茶叶采集雾水之精华，聚大地之灵气，有着“高山出好茶”的自然生态环境。', '[\"https://imgs.qunarzz.com/sight/p0/1805/74/748061410bd7d7a0a3.img.jpg_710x360_1469b69a.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0103o120008jdskbbC6FE.jpg_710x360_f3820588.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0100d120008jt4yvi156D.jpg_710x360_52ebfd8b.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0103e120008k2f5bp76B5.jpg_710x360_cf3c20ac.jpg\", \"https://imgs.qunarzz.com/sight/p0/1805/d9/d975d37f564beab0a3.img.jpg_710x360_7852f55c.jpg\", \"https://imgs.qunarzz.com/sight/p0/1805/b7/b71718e457921a51a3.img.jpg_710x360_06b21766.jpg\"]', '[{\"author\": \"\\u7389*\\u83ab\", \"content\": \"\\u6bd4\\u95e8\\u5e02\\u4ef7\\u8ba2\\u4fbf\\u5b9c\\u8bb8\\u591a\\uff0c\\u91cc\\u9762\\u5c31\\u662f\\u770b\\u8336\\u5c71\\uff0c\\u4e5f\\u6709\\u4e9b\\u4e1c\\u897f\\u5356\\uff0c\\u6709\\u8336\\u6709\\u5403\\u7684\\uff0c\\u6709\\u4e9b\\u5730\\u65b9\\u8fd8\\u5728\\u5efa\\uff0c\\u4e5f\\u86ee\\u6709\\u7279\\u8272\\u7684\\u3002\", \"date\": \"2022-08-21\", \"score\": 5}]', 'https://imgs.qunarzz.com/sight/p0/1805/74/748061410bd7d7a0a3.img.jpg_280x200_1596039a.jpg', '2023-08-03');
INSERT INTO `app_travelinfo` VALUES ('1236', '梦呜苗寨', '4A景区', '9.8', '22', '柳州', '50', '柳州市融水苗族自治县古选村双龙沟内', '梦呜苗寨占地约300亩，是异地搬迁保存完好纯木结构的苗族古寨。', 'https://piao.qunar.com/ticket/detail_1849942150.html', '5.0', '67', '50', '梦呜苗寨占地约300亩，是异地搬迁保存完好纯木结构的苗族古寨。', '[\"https://mp-piao-admincp.qunarzz.com/mp_piao_admin_mp_piao_admin/admin/20217/30d54820c6727b9958d585769ab3c5c2.png_710x360_c2302e12.png\"]', '[]', 'https://mp-piao-admincp.qunarzz.com/mp_piao_admin_mp_piao_admin/admin/20217/30d54820c6727b9958d585769ab3c5c2.png_280x200_7ef61cce.png', '2023-08-03');
