INSERT INTO `app_travelinfo` VALUES ('1252', '北海公园', '4A景区', '10', '3195', '北京', '49', '北京市西城区文津街1号', '白塔湖水相依的皇家园林', 'https://piao.qunar.com/ticket/detail_2131323089.html', '5', '10', '17890', '北海公园位于北京市中心区，城内景山西侧，在故宫的西北面，与中海、南海合称三海。北海公园继承了中国历代的造园传统，博采各地造园技艺所长，兼有北方园林的宏阔气势和江南私家园林婉约多姿的风韵，并蓄帝王宫苑的富丽堂皇及宗教寺院的庄严肃穆，气象万千而又浑然一体，是中国园林艺术的瑰宝。', '[\"https://imgs.qunarzz.com/sight/p0/1507/d2/971ee817d45301a6bb86295899b5e332.water.jpg_710x360_4031bdc8.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/01057120005uowlgg0D41.jpg_710x360_0434921e.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0102o120005upina94FB7.jpg_710x360_cc9cada8.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0106r120005uww9p8E3E6.jpg_710x360_72526ab5.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0106f120005v1tby2F1A8.jpg_710x360_f8126a5c.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/01010120005v3372v6662.jpg_710x360_19503b4f.jpg\"]', '[{\"author\": \"d*0\", \"content\": \"\\u5c0f\\u65f6\\u5019\\u8ba9\\u6211\\u4eec\\u8361\\u8d77\\u53cc\\u6868\\u7684\\u767d\\u5854 \\u771f\\u7684\\u5f88\\u597d\\u770b \\u767d\\u8272\\u5012\\u6620\\u5728\\u6c34\\u4e2d\\u3002\", \"date\": \"2023-02-02\", \"score\": 5}, {\"author\": \"\\u6708*\\u79cb\", \"content\": \"\\u6bdb\\u5bfc\\u4eba\\u5f88\\u597d\\uff0c\\u8bb2\\u89e3\\u4e13\\u4e1a\\u7ec6\\u81f4\\uff0c\\u5e26\\u5b69\\u5b50\\u590d\\u4e60\\u4e86\\u4e00\\u904d\\u5386\\u53f2\\u3002\", \"date\": \"2023-01-17\", \"score\": 5}]', 'https://imgs.qunarzz.com/sight/p0/1507/d2/971ee817d45301a6bb86295899b5e332.water.jpg_280x200_55629ff0.jpg', '2023-08-03');
INSERT INTO `app_travelinfo` VALUES ('1253', '成都大熊猫繁育研究基地', '4A景区', '10', '35557', '成都', '49', '成都市成华区外北熊猫大道1375号', '萌哒哒的国宝在等你', 'https://piao.qunar.com/ticket/detail_2318546286.html', '5', '55', '468977', '成都大熊猫繁育研究基地，是中国政府实施大熊猫等濒危野生动物迁地保护工程的主要研究基地之一，还是我国乃至全球知名的集大熊猫科研繁育、保护教育、教育旅游、熊猫文化建设为一体的大熊猫等珍稀濒危野生动物保护研究机构。这里还常年养着80多只大熊猫以及小熊猫、黑颈鹤、白鹤等珍稀动物。成都大熊猫繁育研究基地位于成都北郊斧头山山侧的浅丘上，这里建有科研大楼、开放研究实验室、兽医院等，形成了以竹木苍翠，鸟语花香，集自然山野风光和优美人工景观为一体，适宜大熊猫及多种珍稀野生动物生息繁衍的生态环境。好了，亲爱的游客，下面我们就一起走进成都大熊猫繁育研究基地参观游览吧。', '[\"https://imgs.qunarzz.com/wugc/p224/201207/13/f40f8a9e4327658c93835fbb.jpg_710x360_7599172e.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0105x1200087dkrut0DBA.jpg_710x360_a9fad2bf.jpg\", \"https://imgs.qunarzz.com/sight/p0/1912/6a/6a2185e7d273612a3.img.jpg_710x360_b2001175.jpg\", \"https://imgs.qunarzz.com/sight/p0/1912/de/de05b4024c3db3eea3.img.jpg_710x360_615d19f3.jpg\", \"https://imgs.qunarzz.com/sight/p0/1912/9b/9b352475592b273aa3.img.jpg_710x360_0594198a.jpg\", \"https://imgs.qunarzz.com/sight/p0/1912/70/70a49c73dab79581a3.img.jpg_710x360_7941edb6.jpg\"]', '[{\"author\": \"\\u7a0b*a\", \"content\": \"\\u4e00\\u8def\\u4e0a\\u7684\\u7f8e\\u666f+\\u5bfc\\u6e38\\u4efb\\u6b22\\u5206\\u6bb5\\u5f0f\\u7684\\u8bb2\\u89e3\\uff0c\\u8ba9\\u8f66\\u7a0b\\u663e\\u5f97\\u4e0d\\u90a3\\u4e48\\u6f2b\\u957f\\u3002\\u56e2\\u9910\\u4e0d\\u597d\\u5403\\uff0c\\u5728\\u6c9f\\u53e3\\u4f4f\\u4e0b\\u540e\\u53eb\\u5916\\u5356\\u5f88\\u65b9\\u4fbf\\uff0c\\u5168\\u7a0b\\u6ca1\\u6709\\u5f3a\\u5236\\u6d88\\u8d39\\uff0c\\u7ed9\\u7684\\u666f\\u70b9\\u89c2\\u5149\\u65f6\\u95f4\\u5f88\\u5145\\u88d5\\u3002\\u7266\\u725b\\u8089\\u5c0f\\u4e32\\u771f\\u7684\\u5f88\\u597d\\u5403\\uff0c\\u559c\\u6b22\\u7684\\u591a\\u5403\\u70b9\\uff0c\\u8fd9\\u4e2a\\u4e00\\u65e6\\u9519\\u8fc7\\u5c31\\u4e0d\\u518d\\u3002\\u9ec4\\u9f99\\u53ef\\u4ee5\\u575a\\u6301\\u7684\\u4e00\\u5b9a\\u8981\\u8d70\\u4e0b\\u6765\\uff0c\\u4e0b\\u5c71\\u7684\\u4e00\\u8def\\u4e0a\\u90fd\\u592a\\u7f8e\\u592a\\u7f8e\\u5566\\uff01\\u5b9e\\