INSERT INTO `app_travelinfo` VALUES ('1263', '西岭雪山', '4A景区', '10', '140', '成都', '49', '成都市大邑县西岭镇西岭雪山风景区', '漫山遍野的杜鹃花', 'https://piao.qunar.com/ticket/detail_4225437748.html', '5', '120', '40208', '西岭雪山位于四川省成都市大邑县境内，距成都仅95公里，总面积375平方公里，属世界自然遗产、大熊猫栖息地、AAAA级旅游景区、国家重点风景名胜区，因唐代大诗人杜甫的千古绝句“窗含西岭千秋雪，门泊东吴万里船”而得名。景区内有终年积雪的大雪山，海拔5364米。西岭雪山属立体气温带，现已形成“春赏杜鹃夏避暑，秋观红叶冬滑雪”的四季旅游格局。景区内旅游资源丰富，优势独特。有云海、日出、森林佛光、日照金山、阴阳界等变幻莫测的高山气象景观，茫茫的原始林海，数不尽的奇花异草，罕见的珍禽异兽，终年不断的激流飞瀑，组成了一个壮观旖旎、神秘奇特的高山自然风景区。春天山花烂漫，杜鹃成林，珙桐花开如鸽子飞翔，三十多个品种的杜鹃延绵数十里，每年4至8月随高差的不同先后开放，漫山遍野，仿若花的海洋；夏天气候凉爽，绿草茵茵25摄氏度，是成都近郊避暑、度假。秋天万山红遍，层林尽染，形成红、黄、绿三色层次渲染的自然景致。冬季积雪期为11月底至次年3月底，雪期约四个月，积雪厚度60厘米，雪质优良，开设了高山滑雪、雪地摩托、蛇形滑雪车、雪上飞碟、雪上滑车、雪上飞片、雪地冲锋舟、狗拉地撬等10余项冰雪游乐项目。', '[\"https://imgs.qunarzz.com/sight/p75/201211/03/c5e69645fb3de0e193835fbb.jpg_710x360_79a8ab8c.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/010371200085xkhj5C4A2.jpg_710x360_ea74b93b.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/100n1f000001gpdpk9AAB.jpg_710x360_7d3a0f88.jpg\", \"https://imgs.qunarzz.com/sight/p0/2002/2b/2b10f0092d2f70a4a3.img.png_710x360_bac25cda.png\", \"https://imgs.qunarzz.com/sight/p0/2002/4a/4a5816ce4ec1fb35a3.img.png_710x360_32e92e2d.png\", \"https://imgs.qunarzz.com/sight/p0/2002/9f/9fa5a79fd53d8ddca3.img.jpg_710x360_e7e78a54.jpg\"]', '[{\"author\": \"\\u53bb*\\u6237\", \"content\": \"\\u76db\\u590f\\u5ddd\\u5185\\u907f\\u6691\\u5f53\\u7136\\u5c11\\u4e0d\\u4e86\\u897f\\u5cad\\u96ea\\u5c71\\u5450\\uff01\\ud83d\\udc4b\\ud83c\\udffb \\u7231\\u7eff\\u8272\\u5927\\u81ea\\u7136\\u7684\\u5c0f\\u4f19\\u4f34\\u901f\\u901f\\u96c6\\u7ed3\\u54af\\uff5e\\u6210\\u90fd\\u5e02\\u533a\\u81ea\\u9a7e2\\u5c0f\\u65f6\\u76f4\\u8fbe\\u5c71\\u4e0b\\u897f\\u5cad\\u9547\\uff0c\\u4e24\\u5929\\u4e00\\u591c\\u73a9\\u8f6c\\u5c71\\u6cb3\\u6e56\\u6cca\\u9732\\u8425\\u97f3\\u4e50\\u8282\\uff0c\\u7b80\\u76f4\\u723d\\u7ffb\\u5929\\ud83e\\udd29\\ud83e\\udd29\\u9732\\u8425\\u73af\\u5883\\u5f88\\u597d\\u6709\\u4e13\\u4e1a\\u7684\\u9732\\u8425\\u56e