INSERT INTO `app_travelinfo` VALUES ('1264', '成都动物园', '未评价', '10', '3458', '成都', '50', '成都市成华区昭觉寺南路234号(动物园地铁站A口步行260米)', '看看金丝猴馆和熊猫馆', 'https://piao.qunar.com/ticket/detail_2449731608.html', '5.0', '20', '74042', '位于成都市北郊，距市区5公里，占地约360亩。园内动物按进化顺序排列，低等的的龟类、两栖类、爬行类、高等的灵长类，应有具有。共有动物3000余头（只），其中四川兽类180余种，鸟类570多种、国内外珍稀动物200多种，设有大熊猫馆，常年有10余只大熊猫在此展出。动物园的人工繁殖大熊猫技术已达国际先进水平，成国内外关注焦点。', '[\"https://imgs.qunarzz.com/sight/p0/201307/23/1d34f4bb7dc4cad3c8d65eac.jpg_710x360_444ad4ab.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/10071f000001h6ysl2959.jpg_710x360_cdc6cfc1.jpg\", \"https://imgs.qunarzz.com/sight/p0/201307/23/5418d79aeb939ef9c8d65eac.jpg_710x360_520c2996.jpg\", \"https://imgs.qunarzz.com/sight/p0/201307/23/2c4fbf53b063be24c8d65eac.jpg_710x360_d210db33.jpg\", \"https://imgs.qunarzz.com/sight/p0/201303/07/ec0cb9a8b24056cbc8d65eac.jpg_710x360_3e8734d1.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/82ad7562c4daf602.jpg_710x360_d516c2d7.jpg\"]', '[]', 'https://imgs.qunarzz.com/sight/p0/201307/23/1d34f4bb7dc4cad3c8d65eac.jpg_280x200_b6e2486d.jpg', '2023-08-03');
INSERT INTO `app_travelinfo` VALUES ('1265', '漫花庄园', '未评价', '5', '4', '成都', '50', '成都市新都区蜀龙大道南段银杏园路口', '踏青赏花网红打卡地', 'https://piao.qunar.com/ticket/detail_1425344491.html', '4', '7', '6871', '成都漫花庄园多年学习借鉴国内外先进花卉种植技术，让游客们不出国门。全年以多种特色花卉展开四季花展，使得景区365天花开不断。春季的郁金香花展、牡丹芍药花展汇聚了百余种荷兰进口精品郁金香和200多个名贵品种的万盆牡丹芍药；夏季的欧洲玫瑰展为您展示500种欧洲进口玫瑰带来的浪漫花海盛宴，还有百米龙沙宝石墙演绎诗人般的浪漫情怀；夏季游园会暨夏季百花展，200多个品种的大丽花、新西兰进口的蓝百合、浪漫迷人的薰衣草花海，还有柳叶马鞭草、超级巨无霸海棠、桑蓓斯凤仙、清新荷花等百余种美丽花卉，给您一个百花齐放的清凉夏日！此外景区还种植了大面积的樱花、桃花、梨花、风信子、仙客来、山茶、玉兰、三角梅、瓜叶菊、鸳鸯茉莉、腊梅等千万株花卉，随季节次第开放，缔造盛世花宴！园内上万株品种各异的百合花与500余种浪漫欧洲玫瑰娇艳绽放，尽展风姿！当高贵纯洁的清新百合邂逅浪漫典雅的欧洲玫瑰，缤纷绚丽的色彩佐以馥郁芬芳的花香，让人仿若走入梦幻仙境，肆意享受鲜花美景带来的欢乐体验。在漫花庄园这片花中乐土，流连忘返,乐享花生活。', '[\"https://imgs.qunarzz.com/sight/p0/1809/75/757b2fc54cfcb19ba3.img.jpg_710x360_c2209184.jpg\", \"https://imgs.qunarzz.com/sight/p0/1809/6a/6a427fccfcf954f4a3.img.jpg_710x360_7bbd3dba.jpg\", \"https://imgs.qunarzz.com/sight/p0/1809/7a/7a80386be28ec13fa3.img.jpg_710x360_4444bfa3.jpg\", \"https://imgs.qunarzz.com/sight/p0/1809/95/95fd2c8d01a0a40fa3.img.jpg_710x360_e125951f.jpg\", \"https://imgs.qunarzz.com/sight/p0/1809/5d/5dc5e52f3b9e4834a3.img.jpg_710x360_4fbbf311.jpg\", \"https://imgs.qunarzz.com/sight/p0/1809/c4/c4a34ed2c68ff296a3.img.jpg_710x360_7d453c1f.jpg\"]', '[{\"author\": \"\\u56e2*\\u5566\", \"content\": \"\\u8d81\\u7740\\u56fd\\u5e86\\u4e4b\\u9645\\u6210\\u90fd\\u75ab\\u60c5\\u6709\\u597d\\u8f6c\\u7684\\u60c5\\u51b5\\u4e0b\\u51fa\\u6765\\u6362\\u6362\\u5fc3\\u60c5\\uff0c\\u8fd8\\u597d\\u8fd9\\u4e24\\u5929\\u5929\\u516c\\u4f5c\\u7f8e\\uff0c\\u770b\\u5230\\u7684\\u666f\\u8272\\u90fd\\u975e\\u5e38\\u96be\\u5f97\\u548c\\u96be\\u5fd8\\uff01\\u53f8\\u673a\\u5e08\\u5085\\u975e\\u5e38\\u5389\\u5bb3\\ud83d\\udc4d\\ud83c\\udffb\\u719f\\u7ec3\\u9a7e\\u9a6d\\u5927\\u5df4\\u5728\\u5c71\\u8def\\u7a7f\\u68ad\\uff0c\\u975e\\u5e38\\u7a33\\uff01\\u611f\\u8c22\\u5bfc\\u6e38\\u59d0\\u59d0\\u7684\\u666f\\u533a\\u53ca\\u76f8\\u5173\\u5386\\u53f2\\u6587\\u5316\\u8bb2\\u89e3\\uff0c\\u975e\\u5e38\\u6709\\u6536\\u83b7\\uff01\\u5bfc\\u6e38\\u59d0\