INSERT INTO `app_travelinfo` VALUES ('1266', '街子古镇', '4A景区', '3.4', '2', '成都', '43', '成都市崇州市惠丰路1号', '千年古镇，有晋代古寺', 'https://piao.qunar.com/ticket/detail_162406902.html', '5', '19.9', '396', '四川省成都市崇州的街子镇，在崇州城西北25公里的凤栖山下，与青城后山连接。它既得山灵水秀之惠，又有以唐代古刹光严禅院为中心的32座寺庙等古迹。 街子是个历史悠久的古镇，五代时名“横渠镇”，因横于味江河畔而得名。境内有晋代古刹———光严禅院，凤栖山旅游风景区，千亩原始森林，千年银杏、千年古楠、清代古塔，清末民初古建一条街，宋代民族英雄王小波起义遗址，唐代一瓢诗人，唐求故居，有神奇传说的古龙潭、五柜沱、云雾洞等，全镇各种文物古迹二十余处。', '[\"https://imgs.qunarzz.com/sight/p0/201405/30/1cd0beb0bee18ab64229a236bf92f9c7.jpg_710x360_9abf5143.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/0103o120008k50ib01FFB.jpg_710x360_ea0b2aa7.jpg\", \"https://qimgs.qunarzz.com/piao_qsight_provider_piao_qsight_web/01013120008ic5bsxA4A5.jpg_710x360_90f3fbc8.jpg\", \"https://imgs.qunarzz.com/sight/p0/1905/7c/7c1b7bc20580d5d5a3.img.jpg_710x360_ec4de7c3.jpg\", \"https://imgs.qunarzz.com/sight/p0/1703/48/48e9286d59988e27a3.water.jpg_710x360_f56225dd.jpg\", \"https://imgs.qunarzz.com/sight/p0/201403/07/7394b2185dc80f31aad840c99ef4e3fe.jpg_710x360_492ddaa6.jpg\"]', '[{\"author\": \"z*u\", \"content\": \"\\u6210\\u90fd\\u51fa\\u53d1\\u8f66\\u7a0b1\\u5c0f\\u65f6\\u5373\\u53ef\\u8fbe\\u5230\\u3002\\u6700\\u5148\\u8fdb\\u5165\\u5546\\u4e1a\\u8857\\uff0c\\u7c73\\u9152\\u5404\\u8272\\u5c0f\\u5403\\u7279\\u8272\\u5546\\u54c1\\u73b2\\u7405\\u6ee1\\u76ee\\u3002\\u7a7f\\u8fc7\\u5546\\u4e1a\\u8857\\u4e34\\u6c34\\u800c\\u5efa\\u6709\\u8bb8\\u591a\\u9910\\u5385\\u8336\\u697c\\u3002\\u5750\\u5728\\u6c34\\u8fb9\\u6765\\u676f\\u78a7\\u6f6d\\u98d8\\u96ea\\uff0c\\u6446\\u54c8\\u9f99\\u95e8\\u9635\\u3002\\u5b89\\u9038\\u7684\\u677f\\u3002\\u8857\\u5b50\\u53e4\\u9547\\u6574\\u4f53\\u5546\\u4e1a\\u5316\\u540c\\u65f6\\u53c8\\u4e0d\\u5931\\u53e4\\u98ce\\u6c14\\u606f\\u3002\\u52a8\\u9759\\u7686\\u5b9c\\u3002\\u4e0d\\u5931\\u4e3a\\u95f2\\u6687\\u51fa\\u6e38\\u7684\\u597d\\u9009\\u62e9\\u3002\", \"date\": \"2023-03-11\", \"score\": 5}, {\"author\": \"\\u542c*\\u6f2b\", \"content\": \"2023.3.22\\u65e5\\u9633\\u5149\\u660e